import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import productBig2 from '../img/shop-details/product-big-2.png';
import detailsPayment from "../img/shop-details/details-payment.png";

const ShopDetailSection = () => {
  const [product, setProduct] = useState('');
  const productData = product;

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const productId = productData?.id || 1; // Fallback to a default product ID
        const response = await fetch(`https://dummyjson.com/products/${productId}`);
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    fetchProduct();
  }, [productData]);

  // if (!product) {
  //   return <div>Loading product details...</div>; // Display a loading indicator
  // }

  return (
    <div>
      {/* <!-- Shop Details Section Begin --> */}
      <section className="shop-details">
        <div className="product__details__pic">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <div className="product__details__breadcrumb">
                  <Link to={`/home`}>{'Home'}</Link>
                  <Link to={`/shop`}>{'Shop'}</Link>
                  <span>{'Product Details'}</span>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-lg-6 offset-lg-3">
                <div className="tab-content">
                  <div className="tab-pane active" id="tabs-1" role="tabpanel">
                    <div className="product__details__pic__item">
                      <img
                        src={product.thumbnail || productBig2} 
                        alt={product.title || 'Product'}
                        style={{ width: '300px', height: 'auto' }}
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="product__details__content">
          <div className="container">
            <div className="row d-flex justify-content-center">
              <div className="col-lg-8">
                <div className="product__details__text">
                  <h4>{product.title}</h4>
                  <div className="rating">
                    {[...Array(Math.ceil(product.rating || 0))].map((_, index) => (
                      <i key={index} className="fa fa-star"></i>
                    ))}
                    {[...Array(5 - Math.ceil(product.rating || 0))].map((_, index) => (
                      <i key={index} className="fa fa-star-o"></i>
                    ))}
                    <span style={{ marginLeft: '10px' }}>{product.rating || 0} Reviews</span>
                  </div>
                  <h3>${product.price}</h3>
                  <p>{product.description}</p>
                  <div className="product__details__cart__option">
                    <div className="quantity">
                      <div className="pro-qty">
                        <input type="text" value="1" readOnly />
                      </div>
                    </div>
                    <a href="#" className="primary-btn">{'Add to cart'}</a>
                  </div>
                  <div className="product__details__btns__option">
                    <a href="#"><i className="fa fa-heart"></i> Add to Wishlist</a>
                    <a href='#'><i className="fa fa-exchange"></i> Add to Compare</a>
                  </div>
                  <div className="product__details__last__option">
                    <h5><span>{'Guaranteed Safe Checkout'}</span></h5>
                    <img src={detailsPayment} alt="Payment Options" />
                    <ul>
                      <li><span>{'Stock Available'}:</span> {product.stock}</li>
                      <li><span>{'Category'}:</span> {product.category}</li>
                      <li><span>{'Tags'}:</span> {product.brand}</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
      {/* <!-- Shop Details Section End --> */}
    </div>
  );
};

export default ShopDetailSection;
