import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import heart from "../img/icon/heart.png";
import compare from "../img/icon/compare.png";
import search from "../img/icon/search.png";

const ShopDetailRelated = () => {
    const [products, setProducts] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch("https://dummyjson.com/products");
                const data = await response.json();
                setProducts(data.products.slice(0, 4)); // Get the first 4 products
            } catch (error) {
                console.error("Error fetching products:", error);
            }
        };

        fetchProducts();
    }, []);

    const handleSearchClick = (product) => {
        navigate(`/shop-details`, { state: { product } });
    };

    return (
        <div>
            {/* <!-- Related Section Begin --> */}
            <section className="related spad">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <h3 className="related-title">{'Related Product'}</h3>
                        </div>
                    </div>
                    <div className="row">
                        {products.map((product) => (
                            <div key={product.id} className="col-lg-3 col-md-6 col-sm-6 col-sm-6">
                                <div className="product__item">
                                    <div
                                        className="product__item__pic"
                                        style={{
                                            backgroundImage: `url(${product.thumbnail})`, // Use 'thumbnail' as product image
                                            backgroundSize: 'contain',
                                            backgroundPosition: 'center',
                                            width: '200px',
                                            backgroundRepeat: 'no-repeat',
                                        }}
                                    >
                                        <ul className="product__hover">
                                            <li><a href="#"><img src={heart} alt="heart" /></a></li>
                                            <li><a href="#"><img src={compare} alt="compare" /> <span>{'Compare'}</span></a></li>
                                            <li><a onClick={() => handleSearchClick(product)}><img src={search} alt="search" style={{ cursor: 'pointer' }} /><span>{'View details'}</span></a></li>
                                        </ul>
                                    </div>
                                    <div className="product__item__text">
                                        <h6>{product.title}</h6>
                                        <a href="#" className="add-cart">+ Add To Cart</a>
                                        <div className="rating">
                                            {[...Array(Math.ceil(product.rating || 0))].map((_, index) => (
                                                <i key={index} className="fa fa-star"></i>
                                            ))}
                                            {[...Array(5 - Math.ceil(product.rating || 0))].map((_, index) => (
                                                <i key={index} className="fa fa-star-o"></i>
                                            ))}
                                        </div>
                                        <h5>${product.price}</h5>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
            {/* <!-- Related Section End --> */}
        </div>
    );
};

export default ShopDetailRelated;
