import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import heart from '../img/icon/heart.png';
import compare from '../img/icon/compare.png';
import search from '../img/icon/search.png';

const ShopSection = () => {
    const [products, setProducts] = useState([]);
    const [categoriesData, setCategoriesData] = useState([]);
    const [currentCategory, setCurrentCategory] = useState('');
    const [currentPage, setCurrentPage] = useState(1);
    const [productsPerPage] = useState(12);
    const location = useLocation();
    const navigate = useNavigate();
    const isKhmer = location.pathname.startsWith('/kh');
    const currentLanguage = location.pathname.split('/')[1] || 'en';

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Fetch all categories and their counts
                const categoryResponse = await fetch('https://example.com/api/categories'); // Replace with your API endpoint
                const categoryData = await categoryResponse.json();
                setCategoriesData(categoryData);

                // Fetch products for the current category
                const productUrl = currentCategory
                    ? `https://example.com/api/products?category=${currentCategory}`
                    : 'https://example.com/api/products'; // Replace with your API endpoint
                const productResponse = await fetch(productUrl);
                const productData = await productResponse.json();
                setProducts(productData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, [currentCategory]);

    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);
    const totalPages = Math.ceil(products.length / productsPerPage);

    const handleCategoryClick = (category) => {
        setCurrentCategory(category);
        setCurrentPage(1);
    };

    const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const handleSearchClick = (product) => {
        navigate(`/${currentLanguage}/shop-details`, { state: { product } });
    };

    return (
        <div>
            <section className="shop spad">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-3">
                            <div className="shop__sidebar">
                                <div className="shop__sidebar__accordion">
                                    <div className="accordion" id="accordionExample">
                                        <div className="card">
                                            <div className="card-heading">
                                                <a data-toggle="collapse" data-target="#collapseOne">
                                                    {isKhmer ? 'ប្រភេទទំនិញ' : 'Categories'}
                                                </a>
                                            </div>
                                            <div
                                                id="collapseOne"
                                                className="collapse show"
                                                data-parent="#accordionExample"
                                            >
                                                <div className="card-body">
                                                    <div className="shop__sidebar__categories">
                                                        <ul className="nice-scroll">
                                                            {categoriesData.map((category) => (
                                                                <li key={category.key}>
                                                                    <a
                                                                        href="#!"
                                                                        onClick={() => handleCategoryClick(category.key)}
                                                                        className={
                                                                            currentCategory === category.key
                                                                                ? 'active-category'
                                                                                : ''
                                                                        }
                                                                    >
                                                                        {isKhmer ? category.kh : category.en} (
                                                                        {category.count || 0})
                                                                    </a>
                                                                </li>
                                                            ))}
                                                        </ul>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-lg-9">
                            <div className="row">
                                {currentProducts.map((product) => (
                                    <div key={product.id} className="col-lg-4 col-md-6 col-sm-6">
                                        <div className="product__item">
                                            <div
                                                className="product__item__pic"
                                                style={{
                                                    backgroundImage: `url(${product.image})`,
                                                    backgroundSize: 'contain',
                                                    backgroundPosition: 'center',
                                                    height: '200px',
                                                    backgroundRepeat: 'no-repeat',
                                                }}
                                            >
                                                <ul className="product__hover">
                                                    <li>
                                                        <a href="#">
                                                            <img src={heart} alt="" />
                                                        </a>
                                                    </li>
                                                    <li>
                                                        <a href="#">
                                                            <img src={compare} alt="" />
                                                            <span>{isKhmer ? 'ប្រៀបធៀប' : 'Compare'}</span>
                                                        </a>
                                                    </li>
                                                    <li>
                                                        <a onClick={() => handleSearchClick(product)}>
                                                            <img src={search} alt="" style={{ cursor: 'pointer' }} />
                                                            <span>{isKhmer ? 'ព័ត៌មានលម្អិត' : 'View details'}</span>
                                                        </a>
                                                    </li>
                                                </ul>
                                            </div>
                                            <div className="product__item__text">
                                                <h6>{product.title}</h6>
                                                <h5>${product.price}</h5>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                            <div className="row">
                                <div className="col-lg-12">
                                    <div className="product__pagination">
                                        {[...Array(totalPages)].map((_, index) => (
                                            <a
                                                key={index}
                                                href="#!"
                                                onClick={() => handlePageChange(index + 1)}
                                                className={currentPage === index + 1 ? 'active' : ''}
                                            >
                                                {index + 1}
                                            </a>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </div>
    );
};

export default ShopSection;
